import React, {useState, useEffect} from 'react'
import './Profile.css'
// import PostDetail from './PostDetail'
import { useParams } from 'react-router-dom'

export default function Userprofile() {
  const {userid} = useParams()
  // console.log(userid)
  const [user, setUser]= useState("")
  const [post, setPost]= useState([])


  useEffect(()=>{
    fetch(`http://localhost:4000/user/${userid}`,{
      headers:{
        "Authorization":"Bearer "+localStorage.getItem("token")
      },
    }).then(res=>res.json())
    .then(result=>{
        // console.log(result)
        setUser(result.user)
        setPost(result.post || [])
    })
  },[userid])

  const followUser = (userId)=>{
    fetch('http://localhost:4000/',{
      method:"put",
      headers:{
        "Content-Type":'application/json',
        "Authorization":"Bearer "+localStorage.getItem("token")
      },
      body:JSON.stringify({
        followId:userId
      })
    }).then((res)=>res.json())
      .then((data)=>{
      console.log(data)
    })
  }
  const UnfollowUser = (userid)=>{
    fetch('http://localhost:4000/unfollow',{
      method:"put",
      headers:{
        "Content-Type":'application/json',
        "Authorization":"Bearer "+localStorage.getItem("token")
      },
      body:JSON.stringify({
        followId:userid
      })
    }).then((res)=>res.json())
      .then((result)=>{
      console.log(result)
    })
  }

  return (
    <div className='profile'>
      <div className="profile-frame">
        <div className="profile-pic">
          <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="oops" />
        </div>
        <div className="profile-data">
          <h1>{user.name}</h1>
          <div style={{display: "flex", alignItems: "center"}}>
          <button className='followbtn' onClick={()=>{followUser(user._id)}}>Follower</button>
          </div>
          <div className="profile-info">
            <p>{post.length} posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{
        width:"100%",
        margin:"25px auto",
        opacity:"0.8"
        }}/>
      <div className="gallery">
        {post.length>0 ? (
          post.map((pics)=>{
            return <img key={pics._id} src={pics.photo} alt='oops' />
          })
        ):(
          <p>No posts Available</p>
        )
        }
      </div>
      {/* {show && 
      <PostDetail item={posts} toggleshow={toggleshow}/>
      } */}
    </div>
  )
}
