import React from 'react'
import "./Createpost.css"

export default function Createpost() {
    const loadFile = (event) =>{
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(event.target.files[0]);
        output.onload = function(){
            URL.revokeObjectURL(output.src)
        };
    };
  return (
    <div className='createPost'>
        <div className="post-header">
            <h4 style={{margin:"3px auto"}}>Create New Post</h4>
            <button id='post-btn'>Upload</button>
        </div>
        <div className="main-div"> 
            <img id='output' />
            <input type='file' accept='image/*' onChange={(event)=>{(loadFile())}}/>
        </div>
        <div className="details">
            <div className="card-header">
                <div className="card-pic">
                <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="oops" />
                </div>
                <h5>Amit</h5>
            </div>
            <textarea type='text' placeholder='write a caption...'></textarea>
        </div>
    </div>
  )
}
