import React, { useEffect, useState } from 'react'
import { json, useNavigate } from 'react-router-dom'
import './Home.css'
// import { post } from '../../../backend/routes/createPost';

export default function Home() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token")
    if (!token) {
      navigate('/signin')
    }

    fetch("http://localhost:4000/allposts", {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("token")
      },
    }).then(res => res.json())
      .then(result => setData(result))
      .catch(err => console.log(err))

  }, [])

  const likePost = (id) => {
    fetch('http://localhost:4000/likes', {
      method: 'put',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + localStorage.getItem('token'),
      },
      body: JSON.stringify({
        postId: id,
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((result) => {
        const newData = data.map((post) => {
          if (post._id == result._id) {
            return result
          }
          else {
            return post
          }
        })
        // console.log('Like result:', result)
        setData(newData)
      })
      .catch((err) => console.log('Fetch error:', err));
  };

  const unlikePost = (id) => {
    fetch('http://localhost:4000/unlikes', {
      method: 'put',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + localStorage.getItem('token'),
      },
      body: JSON.stringify({
        postId: id,
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((result) => {
        const newData = data.map((post) => {
          if (post._id == result._id) {
            return result
          }
          else {
            return post
          }
        })
        // console.log('unLike result:', result)
        setData(newData)
      })
      .catch((err) => console.log('Fetch error:', err));
  };

  const makeComment = (text, id) => {
    fetch('http://localhost:4000/comment', {
      method: 'put',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + localStorage.getItem('token'),
      },
      body: JSON.stringify({
        text: text,
        postId: id,
      })
    })
      .then((res) => { res.json() })
      .then((result) => { console.log(result) })
  }

  return (
    <div className="main">
      {data.map((post) => {
        return (
          <>
            <div className='card'>
              <div className="card-header">
                <div className="card-pic">
                  <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="oops" />
                </div>
                <h5 className='profile-name'>{post.postedBy.name}</h5>
              </div>
              <div className="card-image">
                <div className="img-size">
                  <img src={post.photo} alt="oops" srcset="" />
                </div>
              </div>
              <div className="card-content">
                <div className='like'>
                  {
                    post.like.includes(JSON.parse(localStorage.getItem('user'))._id)
                      ? (
                        <span class="material-symbols-outlined material-symbols-outlined-red" onClick={() => { unlikePost(post._id) }}>favorite</span>
                      ) : (
                        <span class="material-symbols-outlined" onClick={() => { likePost(post._id) }}>favorite</span>
                      )
                  }

                  <p className='no-like'>{post.like.length}Like</p>
                  <span class="material-symbols-outlined add">person_add</span>
                </div>
                <h3 className="book-name">{post.title}</h3>
                <div className="book-week">
                  <p>per week ₹.</p>
                  <p className="week-charge"> 00</p>
                </div>
              </div>
              <div className="add-comment">
                <span className='material-symbols-outlined emoji'>mood</span>
                <input type="text" placeholder='Add message' value={comment} onChange={(e) => { setComment(e.target.value) }} />
                <button className="material-symbols-outlined send" onClick={() => { makeComment(comment, post._id) }}>send</button>
              </div>
            </div>
          </>
        )
      })}
      <div className='showComment'>
        <div className='container'>
          <div className='postPic'>
            <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="" />
          </div>
          <div className="details">
            <div className="card-header" style={{borderBottom: "1px sold #00000029"}}>
              <div className="card-pic">
                <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="oops" />
              </div>
              <h5 className='profile-name'>abc</h5>
            </div>
           <div className="comment-section">
           </div>
            <div className="card-content">
              <p>Likes</p>
              <p>Awesome post</p>
            </div>
            <div className="add-comment">
              <span className='material-symbols-outlined emoji'>mood</span>
              <input type="text" placeholder='Add message' value={comment} onChange={(e) => { setComment(e.target.value) }} />
              <button className="material-symbols-outlined send">send</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
