import React from 'react'
import { useRef, useState, useEffect } from 'react'
import './ProfilePic.css'

export default function Profilepic({changePic}) {
const [title, setTitle] = useState("");
const [image, setImage] = useState("");
const [url, setUrl] = useState("");


    const handleFile= useRef(null)
    const handleClick =()=>{
        handleFile.current.click()
    }

    const postData = () => {
        const data = new FormData()
        data.append('file',image)
        data.append("upload_preset","bookexchange")
        data.append("cloud_name","okcloud")
        fetch("https://api.cloudinary.com/v1_1/okcloud/image/upload",
            {
                method:"post",
                body:data
            }).then(res=>res.json())
            .then(data=>setUrl(data.url))
            .catch(err=>console.log(err))
        console.log(url)
    }

    const profilepicurl=()=>{
        fetch('http://localhost:4000/uploadprofilepic', {
            method: 'put',
            headers: {
              'Content-Type': 'application/json',
              Authorization: 'Bearer ' + localStorage.getItem('token'),
            },
            body: JSON.stringify({
              pic:url
            }),
          })
            .then((res) =>res.json())
            .then((data) =>{
                console.log(data)
                changePic();
                window.location.reload()
            })
            .catch(err=>console.log(err))
    }

    useEffect(()=>{
        if(image){
            postData()
        }
    },[image])

    useEffect(()=>{
        if(url){
            profilepicurl()
        }
    },[url])
   
  return (
    <div className='profilepic darkBg'>
        <div className="changePic centered">
            <div>
                <h1>Change Profile Photo</h1>
            </div>
            <div style={{borderTop:'1px solid #00000030'}}>
                <button className='uploadbtn' style={{color: "#1EA1F7"}} onClick={handleClick}>Upload </button>
                <input type="file" ref={handleFile} accept='image/*' onChange={(e)=>{
                    setImage(e.target.files[0])
                }} style={{display: "none"}} /> 
            </div>
            <div style={{borderTop:'1px solid #00000030'}}>
                <button className='uploadbtn' style={{color: 'red '}}>Remove Photo</button>
            </div>
            <div style={{borderTop:'1px solid #00000030'}}>
                <button className='uploadbtn' style={{}} onClick={changePic}>Cancel</button>
            </div>
        </div>
    </div>
  )
}
