import React, {useState, useEffect} from 'react'
import './Profile.css'
import PostDetail from './PostDetail'
import { useParams } from 'react-router-dom'

export default function Userprofile() {
  const {userid} = useParams()
  console.log(userid)
  const [user, setUser]= useState("")
  const [posts, setPosts]= useState([])

//   const toggleshow=(posts)=>{
//     if(show){
//       setShow(false)
//     }else{
//       setShow(true)
//       setPosts(posts)
//     }
//   }

  useEffect(()=>{
    fetch(`http://localhost:4000/user/${userid}`,{
      method:'get',
      headers:{
        "Authorization":"Bearer "+localStorage.getItem("token")
      },
    }).then(res=>res.json())
    .then(result=>{
        console.log(result)
        setUser(result.user)
        setPosts(result.posts)
    })

  },[])
  return (
    <div className='profile'>
      <div className="profile-frame">
        <div className="profile-pic">
          <img src="https://th.bing.com/th/id/OIP.SAcV4rjQCseubnk32USHigHaHx?rs=1&pid=ImgDetMain" alt="oops" />
        </div>
        <div className="profile-data">
          <h1>{user.name}</h1>
          <div className="profile-info">
            <p>{posts.length} post</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{
        width:"100%",
        margin:"25px auto",
        opacity:"0.8"
        }}/>
      <div className="gallery">
      {posts.map((pics)=>{
        return <img key={pics._id} src={pics.photo}></img>
      })}   
      </div>
      {/* {show && 
      <PostDetail item={posts} toggleshow={toggleshow}/>
      } */}
    </div>
  )
}
